// import logo from './logo.svg';
import './App.css';
import React from 'react';

// function Nav(props){
//     return ( <h1> hallo {props.name}</h1> );}

function Main(){
  return ( <h2> Main </h2> );}


class Ap extends React.Component {
  constructor(props){
    super(props)
    this.state = {time : props.start}
  }

  DidMout(){
    this.addInterval = setInterval( ()=> this.inc(),1000)
  }

  unmout(){
    clearInterval(this.addInterval)
  }

  inc(){
    this.setstate((state,props) => ({
      time: parseInt( state.time ) + 1
    }));
  }

  render() {
    return (
      <React.Fragment>
        <h1>{this.state}</h1>
      </React.Fragment>
  );}}

class App extends React.Component {
  render() {
    return (
      <React.Fragment>
        <Main />
        <Ap start="0"/>
      </React.Fragment>
  );}}


export  { Ap, App }






















